
public class breakAndcontinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//for loop:
		
		for (int i=0;i<10;i++) {
			//System.out.println("value of i is:" +i);
			if(i==2) {
				break;
			}
			System.out.println("value of i is:" +i);
		}
		for (int j=0;j<10;j++) {
			//System.out.println("value of i is:" +i);
			if(j==2) {
				continue;
			}
			System.out.println("value of j is:" +j);
	}

}
}

		
	